import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import {Provider} from "react-redux";
import store from "./Store/Store";
import './App.css';
import fire from './config/fire';
import Login from './Login.js';
import Home from './Home.js';
// page
import OnlineProduct from "./OnlineProduct/index";
import Cart from "./Cart/index";
import Food from "./Food/index";



class App extends Component {

    constructor(props) {
    super(props);
    this.state = {
      user: null,
    };
    this.authListener = this.authListener.bind(this);
  }
  componentDidMount() {
    this.authListener();
  }
  authListener() {
    fire.auth().onAuthStateChanged((user) => {
      if (user) {
        this.setState({ user });
      } else {
        this.setState({ user: null });
      }
    })
  }
  render() {
    return (
      <Router>

<div className="container">
<div className="navnvbar">
  <div className="navbar">

      <div className="links">
        <Link to="/Food" className="barr">Food</Link>
        <Link to="/Cart" className="barr">Cart</Link>
        <Link to="/Login" className="barr">Login</Link>


        <div className="Appp">
        { this.state.user ? ( <Home /> ) : ( <Login /> ) }
        </div>

      </div>
    </div>
  </div>
</div>

<Route path="/Food" component={Food} exact />
<Route path="/Food/:id" component={OnlineProduct} exact />
<Route path="/Cart" component={Cart} exact />
<Route path="/Login" component={Login} exact />


      </Router>



    );
  }
}

function AppWithStore(){
  return <Provider store={store}>
    <App />
  </Provider>
}

export default AppWithStore;
